import React, {Component} from 'react';
import './Post.css';
import axios from 'axios';
import {
    BrowserRouter as Router,
    Route,
    Link,
    Switch
  } from 'react-router-dom';
 class PostComponent extends Component {

    constructor() {
        super();
        this.state = {
            showMenu: false,
        }

        this.showMenu = this.showMenu.bind(this);
        this.closeMenu = this.closeMenu.bind(this);
    }

    showMenu(event) {
        event.preventDefault();
        this.setState({ showMenu: true }, () => {
            document.addEventListener('click', this.closeMenu);
        });
    }

    closeMenu() {
        this.setState({ showMenu: false }, () => {
          document.removeEventListener('click', this.closeMenu);
        });
    }


      render() {
        return(
            <div className="postcontainer">
             <form action={`http://localhost:3000/users/post`} method="post">   
            <div className="postline1">
            <div className="postname">
            <div className="picon" style={{fontSize: '70%'}}>
               <Link to='/'>
                        <img src={require('./images/lv.png')} className="pimagestyle" alt="avatar" />
                </Link>
            </div>&nbsp; &nbsp;
               <a href="#">Drafts</a> &nbsp; &nbsp;
               <a href="#">Saved</a>
            </div>
            <div className="posticons">
                <a href="#">&hellip;</a>
                &nbsp; &nbsp;
                {/* <a href="#" className="fa fa-bell"></a> */}
                &nbsp; &nbsp;
                {/* <div>
                <button onClick={this.showMenu} className="p-circle">p</button>
