  //             });
  //         }).catch((e) => {
  //             alert(e)
  //         });
  // }






  render() {
    { console.log("is user loggedin", localStorage.getItem("isLoggedIn")) }
    return (
      <div className="Hmain-container">
        <div className="det1">
          <div className="img1">
            <Link to='/'>
              <img src={require('./images/learnhigh.jpeg')} style={{ color: 'black' }} alt="avatar" />
            </Link>
          </div>
          <div className="logo" style={{ fontSize: '130%', fontStretch: 'ultra-condensed', fontFamily: 'italic', width: '60%' }}>
            <h1>learnHigh</h1>
          </div>
        </div>
        <div className="det">
          {this.state.isUserLoggedIn ?
            <div>
              <div className="login-form">
                <Link className="nav-link" to='/login'>
                  <a style={{ color: 'green', textDecorationLine: 'none' }} id="Signin" onClick={this.handleSignin}>signout</a>
                </Link>
              </div>
            </div>
            :
              <div>
                <div className="mem">
                  <a id="welcome">Become a Member</a>
                </div>
                <div className="login-form">
                  <Link className="nav-link" to='/login'>
                    <a style={{ color: 'green', textDecorationLine: 'none' }} id="Signin">signin</a>
                  </Link>
                </div>
          </div>
        }
       &nbsp;&nbsp;&nbsp;
            <div className="login-form1">
            <Link className="nav-link" to='/post'>
              <a style={{ color: 'green', textDecorationLine: 'none' }} className="rect">Add Post</a>
            </Link>
          </div>
        </div>
      </div>
    );
  }
}


export default HeaderComponent;
