import React, {Component} from 'react';
import './App.css';
import axios from 'axios';
import {
    BrowserRouter as Router,
    Route,
    Link,
    Switch
  } from 'react-router-dom';
  import LoginComponent from './LoginComponent';
  import DataComponent from './DataComponent';
  import PageComponent from './PageComponent';
  import PostComponent from './PostComponent';
  // import SigninComponent from './SigninComponent';
  // import SignoutComponent from './SignoutComponent';
 class HeaderComponent extends Component {

  constructor(props) {
    super(props);
    this.state = {
      data: [{
        title: "...",
        titleDescription: "...",
        authorName: "...",
        postDescription: "...",
        createdAt: "...",
        user: {
            email: "...",
            userName: "..."
        }
    }]

      };
    }

    handleSignin = () => {
      document.getElementById('Signin').innerHTML = "signin"
      window.localStorage.setItem("isLoggedIn","false")

      // window.localStorage.setItem("userName","Null")
      // window.localStorage.setItem("userid","Null")
    }

    componentDidMount() {
      //this.fetch();
      var userName = window.localStorage.getItem("isLoggedIn");
      if(userName === "true" ) {
        document.getElementById('Signin').innerHTML = "Sign out"
      } else {
        document.getElementById('Signin').innerHTML = "Sign in"
      }
    }

    fetch() {
      var url = "http://localhost:3000/users/posts";


      axios.get(url)
            .then((response) => {
              console.log(response)
              this.setState({
                data: response.data.data           
              });  
            }).catch((e) => {
              alert(e)
            });
          }




      render() {
        console.log(this.state.data[0].user.userName)
        return(
        <div className="Hmain-container">
          <div className="det1">
            <div className="img1">
            <Link to='/'>
              <img src={require('./images/learnhigh.jpeg')} style={{color: 'black'}} alt="avatar" />
            </Link>
            </div>
            <div className="logo" style={{fontSize: '130%', fontStretch: 'ultra-condensed', fontFamily: 'italic', width: '60%'}}>
                <h1>learnHigh</h1>
            </div> 
          </div>
          <div className="det">

            <div className="mem">
                <a href="#">Hello {this.state.data[0].user.userName}</a> 
            </div> &nbsp;&nbsp;&nbsp;
            <div className="login-form">
            <Link className="nav-link" to='/login'>
                <a style={{color: 'green', textDecorationLine: 'none'}} id="Signin" onClick={this.handleSignin}>signin</a> 
            </Link>  
            </div> &nbsp;&nbsp;&nbsp;
            <div className="login-form1">
            <Link className="nav-link" to='/post'>         
                <a style={{color: 'green', textDecorationLine: 'none'}} className="rect">Add Post</a>
            </Link>
            </div>
          </div>            
        </div>  
        );
      }
   }


export default HeaderComponent;
