import React, {Component} from 'react';
import './App.css';
import axios from 'axios';
import {
    BrowserRouter as Router,
    Route,
    Link,
    Switch
  } from 'react-router-dom';
  import LoginComponent from './LoginComponent';
  import DataComponent from './DataComponent';
  import PageComponent from './PageComponent';

 class HomeComponent extends Component {

  constructor() {
    super();
    this.state = {
      data: []
    }
  }

  componentWillMount() {
    this.fetch();
  }

  fetch() {
    var url="http://localhost:3000/users/posts";

    alert(url)

    axios.get(url)
          .then((response) => {
            console.log(response)
            this.setState({
              data: response.data.data           
            });  
          }).catch((e) => {
            alert(e)
          });
        }



      render() {
        console.log(this.state)
        var allposts = this.state.data.map(post => <Post key={post.id} post={post}/>);
        return(
        <div className="container">
        <div className="container1">
        <div className="main-container">
            <div className="logo"> 
                <h1>Medium</h1>
            </div> &nbsp; &nbsp;

            <div className="searchbox">
                <i className="fas fa-search"></i>
            </div> &nbsp; &nbsp;
            <div className="mem">
                <a href="#">Become a Member</a> 
            </div> &nbsp; &nbsp;
            <Link className="nav-link" to='/login'>
            <div className="login-form">
                <a href="#">Sign in</a> 
            </div> &nbsp; &nbsp;
            </Link>

            <div className="login-form">
                <a href="#" className="rect">Get started</a>
            </div>

        </div>  
        <div className="tags">
            <a href="#">HOME</a>
            &nbsp; 
            <a href="#">ONEZERO</a>
            &nbsp;  
            <a href="#">ELEMENTAL</a> 
            &nbsp; 
            <a href="#">HEATED</a>
            &nbsp;
            <a href="#" className="t1">TECH</a> 
            &nbsp; 
            <a href="#" className="t1">STARTUPS</a>
            &nbsp; 
            <a href="#" className="t1">SELF</a> 
            &nbsp; 
            <a href="#" className="t1">POLITICS</a> 
            &nbsp; 
            <a href="#" className="t1">HEALTH</a>
            &nbsp; 
            <a href="#" className="t1">DESIGN</a> 
            &nbsp; 
            <a href="#" className="t1">HUMAN PARTS</a>
            &nbsp; 
            <a href="#" className="t1">MORE</a>  

        </div> 
        </div>
        <div className="container2" id="post1">
          {allposts}
        </div>
        <br />
     </div>
        );
      }
   }




   class Post extends Component {
    constructor(props) {
        super(props);

      }




      render() {
        console.log(this.props.post.id)
        return(
    <div className="container2" id="post1">

            <div className="para">

                <Link className="nav-link" to='/singlepage' params={{value: this.props.post.id}}>
                <div className="mainb">
                    <h1>{this.props.post.titleDescription}</h1>
                </div>
                <div className="b1">
                    <p>{this.props.post.postDescription}</p>
                </div>
                </Link>
                <Link className="nav-link" to='/comment'>

                <div className="auth">
                    <p>{this.props.post.authorName}</p>
                </div>
                </Link>
                <div className="new">
                <div className="pub">
                    <p>{this.props.post.date}  .  {this.props.post.readTime} &#9733;</p>
                </div>
                <div className="resp">

                    <i class="material-icons">&#xe866;</i>
                </div>
                </div>
            </div> <br/>
   </div>
        );
    }

   }

export default HomeComponent;
