import React, {Component} from 'react';
import './App.css';
import axios from 'axios';
import {
    BrowserRouter as Router,
    Route,
    Link,
    Switch
  } from 'react-router-dom';
  import LoginComponent from './LoginComponent';
  import DataComponent from './DataComponent';
  import PageComponent from './PageComponent';

 class HomeComponent extends Component {

  constructor() {
    super();
    this.state = {
      //tag: this.props.post.location.state.search,
      data: []
    }
  }

  componentDidMount() {
    this.fetch();
  }

  fetch() {
    //var url="http://localhost:3000/users/posts";
    //console.log("locations params", this.props.location.search)
    if(this.props.location.search){
      console.log("fetching", this.state.props.location.search)
      var url = "http://localhost:3000/users/posts?tag=${this.props.location.search}";
    } else {
      console.log("fetching all")
      var url = "http://localhost:3000/users/posts";
    }

    // alert(url)

    axios.get(url)
          .then((response) => {
            // console.log(response)
            this.setState({
              data: response.data.data           
            });  
          }).catch((e) => {
            alert(e)
          });
        }



      render() {
        console.log(this.state)
        //console.log(this.props)
        var allposts = this.state.data.map(post => <Post key={post.id} post={post}/>);
        return(
        <div className="container">
        <div className="container1">
        <div className="main-container">
            <div className="logo"> 
                <h1>Medium</h1>
            </div> &nbsp; &nbsp;

            <div className="searchbox">
                <i className="fas fa-search"></i>
            </div> &nbsp; &nbsp; 
            <div className="mem">
                <a href="#">Become a Member</a> 
            </div> &nbsp; &nbsp;
            <Link className="nav-link" to='/login'>
            <div className="login-form">
                <a href="#">Sign in</a> 
            </div> &nbsp; &nbsp;
            </Link>

            <div className="login-form">
                <a href="#" className="rect">Get started</a>
            </div>

        </div>  
        <div className="tags">
            <a href="#">HOME</a>
            &nbsp; 
            <Link className="nav-link" to={{ pathname: '/tag', search: "?ONEZERO"}}>
            ONEZERO
            &nbsp;  
            </Link>
            <Link className="nav-link" to={{ pathname: '/tag', search: "?ELEMENTAL"}}>
            <a href="#">ELEMENTAL</a> 
            </Link>
            &nbsp; 
            <a href="#">HEATED</a>
            &nbsp;
            <a href="#" className="t1">TECH</a> 
            &nbsp; 
            <a href="#" className="t1">STARTUPS</a>
            &nbsp; 
            <a href="#" className="t1">SELF</a> 
            &nbsp; 
            <a href="#" className="t1">POLITICS</a> 
            &nbsp; 
            <a href="#" className="t1">HEALTH</a>
            &nbsp; 
            <a href="#" className="t1">DESIGN</a> 
            &nbsp; 
            <a href="#" className="t1">HUMAN PARTS</a>
            &nbsp; 
            <a href="#" className="t1">MORE</a>  

        </div> 
        </div>
        <div className="container2" id="post1">
          {allposts}
        </div>
        <br />
     </div>
        );
      }
   }




   class Post extends Component {
    constructor(props) {
        super(props);

      }




      render() {
        //console.log(this.props.post.id)
        var Date = this.props.post.createdAt
        Date = Date.slice(0,10)
        return(
    <div className="container2" id="post1">

            <div className="para">
            {/* <Link className="nav-link" to={{ pathname: '/singlepage', state: { p:this.props.post.id} }} > */}
            <Link className="nav-link" to={{ pathname: '/singlepage', state: { p:this.props.post.id} }} >
                {/* <Link className="nav-link" to='/singlepage' params={{value: this.props.post.id}}> */}
                <div className="mainb">
                    <h1>{this.props.post.titleDescription}</h1>
                </div>
                <div className="b1">
                    <p>{this.props.post.postDescription}</p>
                </div>
                </Link>
                <Link className="nav-link" to={{ pathname: '/comment', state: {p:this.props.post.id}}}>

                <div className="auth" style={{textDecoration: "underline"}}>
                    <p>{this.props.post.authorName}</p>
                </div>
                </Link>
                <div className="new">
                <div className="pub">
                    <p>{Date}  .  {this.props.post.readTime} &#9733;</p>
                </div>
                <div className="resp">                    
                    <i class="material-icons">&#xe866;</i>
                </div>
                </div>
            </div> <br/>
   </div>
        );
    }

   }

export default HomeComponent;
